import React, { Component } from "react";
import { AccountIcon } from "mdi-react";
import { css } from "emotion";
import { Link } from "react-router-dom";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profiles: {},
      boy: undefined
    };
  }

  componentDidMount() {
    this.getProfiles();
    this.getBoy(1);
  }

  async getProfiles() {
    const apiUrl = `http://localhost:5000/api/boys/`;

    await fetch(apiUrl)
      .then(response => {
        return response.json();
      })
      .then(data => {
        const profiles = data;
        this.setState({ profiles: profiles });
      });
  }

  async getBoy(id) {
    const apiUrl = `http://localhost:5000/api/boys/${id}`;

    await fetch(apiUrl)
      .then(response => {
        return response.json();
      })
      .then(data => {
        const boy = data;
        console.log(boy);
      });
  }

  render() {
    const { boys } = this.state;

    return (
      <div className={container()}>
        <div
          style={{
            width: "100%",
            display: "flex",
            flexWrap: "wrap",
            border: "1px solid gray"
          }}
        >
          {boys.length > 0 &&
            profiles.map((obj, index) => (
              <div key={index} className="card">
                <div className="card-top">
                  <h2>{obj.name}</h2>
                  <h2>{`${obj.age} år`}</h2>
                </div>
                <div>
                  <img
                    className="image"
                    src={
                      process.env.PUBLIC_URL + `/profileImages/${obj.image}.jpg`
                    }
                  />
                </div>
                <div className="description">{obj.description}</div>
                <Link to={`/profile/:${obj.id}`}>
                  <button>
                    <AccountIcon />
                    Se min profile
                  </button>
                </Link>
              </div>
            ))}
        </div>
      </div>
    );
  }
}

const container = () => css`
  .card {
    padding: 0 1rem 1.5rem 1rem;
    width: 100%;

    .card-top {
      display: flex;
      justify-content: space-between;
      padding-right: 1rem;
    }

    button {
      cursor: pointer;
      width: 100%;
      padding: 0.5rem;
      margin: 0.5rem 0 0.5rem 0;
      border: 1px solid gray;
      background-color: white;

      span {
        font-size: 1rem;
        text-align: top;
      }

      svg {
        padding-right: 1rem;
      }
    }

    .description {
      padding: 0.5rem 0 0.5rem 0;
    }

    img {
      padding: 0.5rem 0 0.5rem 0;
      width: 100%;
      max-width: 1000px;
    }
  }
`;

export default Home;
