import React, { useState, Component } from "react";
import axios from "axios";
import TextInput from "./ui/TextInput";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";

//css
import { css } from "emotion";
import { ErrorIcon } from "mdi-react";

//{}
//[]

// Edge case 1: Create account if user already exists

class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loginName: "",
      loginPassword: "",
      createAccount: false,
      createAccountName: "",
      createAccountPassword: "",
      createAccountAge: ""
    };
  }

  handleFormChange = evt => {
    this.setState({ [evt.target.name]: evt.target.value });
  };

  handleSubmitLogin = evt => {
    evt.preventDefault();
    const { loginName, loginPassword } = this.state;

    this.setState({ valTxtPassword: "", valTxtName: "" });
    loginName === "" && this.setState({ valTxtName: "Navn skal være udfyldt" });
    loginPassword < 3 &&
      this.setState({
        valTxtPassword: "Password skal mindst være 3 karakterer"
      });

    if (loginName && loginPassword) {
      this.props.setAuthed(
        true,
        this.state.loginName,
        this.state.loginPassword
      );
      this.setState({ loginName: "", loginPassword: "" });
    }
  };

  handleSubmitCreateAccount = async evt => {
    evt.preventDefault();

    // create copy of state?
    const newAccount = {
      name: this.state.createAccountName,
      password: this.state.createAccountPassword,
      age: this.state.createAccountAge
    };

    this.props.createAccount(newAccount);
  };

  render() {
    const { authed } = this.props;
    const {
      loginName,
      loginPassword,
      createAccountName,
      createAccountPassword,
      createAccountAge
    } = this.state;

    return (
      <div className={container()}>
        {authed.isAuthed && (
          <div>
            {console.log(authed)}
            <h1>Profil oplysninger</h1>
            <h2>Navn</h2>
            <p>{authed.profileName && authed.profileName}</p>
            <h2>Alder</h2>
            <p>{authed.profileAge && authed.profileAge}</p>
            <FormButton
              label="log ud"
              style={{ marginTop: "1rem" }}
              handleSubmit={this.props.handleLogOut}
            />
          </div>
        )}
        {!authed.isAuthed && (
          <>
            <form>
              <h1>Log ind</h1>
              <h2>Brugernavn</h2>
              <TextInput
                name="loginName"
                value={loginName}
                handleChange={this.handleFormChange}
                initText="Brugernavn"
              />

              {this.state.valTxtName && (
                <div className="validation-text">{this.state.valTxtName}</div>
              )}
              <h2>Kodeord</h2>
              <TextInput
                name="loginPassword"
                value={loginPassword}
                handleChange={this.handleFormChange}
                initText="Kodeord"
              />
              {this.state.valTxtPassword && (
                <div className="validation-text">
                  {this.state.valTxtPassword}
                </div>
              )}

              <FormButton
                label="log ind"
                style={{ marginTop: "1rem" }}
                handleSubmit={this.handleSubmitLogin}
              />
            </form>
            {!this.state.createAccount && (
              <div>
                <FormButton
                  label="Lav en ny profil"
                  style={{ marginTop: "1rem" }}
                  handleSubmit={() => {
                    this.setState({ createAccount: true });
                  }}
                />
              </div>
            )}
            {this.state.createAccount && (
              <form>
                <h1>Opret bruger</h1>
                <h2>Navn</h2>
                <TextInput
                  name="createAccountName"
                  value={createAccountName}
                  handleChange={this.handleFormChange}
                  initText="Navn"
                />
                <h2>Password</h2>
                <TextInput
                  name="createAccountPassword"
                  value={createAccountPassword}
                  handleChange={this.handleFormChange}
                  initText="Password"
                />
                <h2>Alder</h2>
                <TextInput
                  name="createAccountAge"
                  value={createAccountAge}
                  handleChange={this.handleFormChange}
                  initText="Alder"
                />

                <FormButton
                  handleSubmit={this.handleSubmitCreateAccount}
                  label="Send"
                  style={{ marginTop: "1rem" }}
                />
                {this.props.errorMessage && (
                  <p>
                    <ErrorIcon /> {this.props.errorMessage}}
                  </p>
                )}
              </form>
            )}
          </>
        )}
      </div>
    );
  }
}

const container = () => css`
  .list {
    margin-top: 1rem;

    .message {
      padding-bottom: 1rem;
    }
  }

  h1 {
    font-size: 1.5rem;
  }

  h2 {
    font-size: 1rem;
  }

  .validation-text {
    padding: 1rem 0 1rem 0;
  }
`;

export default Account;
