import React, { useState, useEffect } from "react";
// router
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";
// css
import { css } from "emotion";
// components
import axios from "axios";
import Header from "./components/Header";
import Main from "./components/Main";
import MessageBoard from "./components/MessageBoard";
import Home from "./components/Home";
import Profile from "./components/Profile";
import Account from "./components/Account";

const App = () => {
  const [isAuthed, setIsAuthed] = useState(false);
  const [authedProfile, setAuthedProfile] = useState({});

  useEffect(() => {
    localStorage.getItem("authedProfile") &&
      setAuthedProfile(localStorage.getItem("authedProfile"));
  });

  const setAuthed = (login, userName, password) => {
    if (login === false) {
      setIsAuthed(false);
      setAuthedProfile({});
    } else if (login === true && userName && password)
      getProfileRequest(userName);
  };

  const getProfileRequest = async username => {
    const reqUrl = `http://localhost:5000/api/auth/${username}`;

    await axios
      .get(reqUrl)
      .then(async function(response) {
        console.log(response.data);
        setIsAuthed(true);
        await setAuthedProfile(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  setLocalStorage = () => {
    localStorage.setItem("authedProfile", JSON.stringify(authedProfile.name));
  };

  return (
    <Router>
      <div className={container()}>
        <Header authedProfile={authedProfile} />
        <Main className="main">
          <Switch>
            <Route
              path="/account"
              render={props => (
                <Account
                  {...props}
                  isAuthed={isAuthed}
                  authedProfile={authedProfile}
                  setAuthed={setAuthed}
                />
              )}
            />
            <Route path="/profile/:id" component={Profile} />
            {/*<Route path="/messages" component={MessageBoard} />*/}
            <Route
              path="/messages"
              render={props => (
                <MessageBoard
                  {...props}
                  isAuthed={isAuthed}
                  authedProfile={authedProfile}
                />
              )}
            />
            <Route exact path="/frontpage" component={Home} />
            <Route path="/profile/:id" component={Profile} />
            <Route path="/" render={() => <Redirect to="/frontpage" />} />
          </Switch>
        </Main>
      </div>
    </Router>
  );
};

const container = () => css`
  header {
    display: flex;

    div {
      padding: 1rem;
    }

    span {
      vertical-align: super;
    }

    svg {
      padding-right: 1rem;
    }
  }
`;

export default App;
