import React, { useState, Component } from "react";
import axios from "axios";
import TextInput from "./ui/TextInput";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";

//css
import { css } from "emotion";

//{}
//[]

class Account2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loginName: "",
      loginPassword: "",
      createAccount: false,
      createAccountName: "",
      createAccountPassword: "",
      createAccountAge: ""
    };
  }

  handleFormChange = evt => {
    console.log(evt.target.name + " " + evt.target.value);

    this.setState({ [evt.target.name]: evt.target.value });
  };

  handleSubmitLogin = evt => {
    evt.preventDefault();
    console.log(
      "Submit: " + this.state.loginName + " " + this.state.loginPassword
    );
    this.props.setAuthed(true, this.state.loginName, this.state.loginPassword);
    this.setState({ loginName: "", loginPassword: "" });
  };

  sendPostRequestCreateProfile = async profile => {
    const reqUrl = "http://localhost:5000/api/profiles";

    await axios
      .post(reqUrl, profile)
      .then(function(response) {
        console.log(response);
        //setAddedProfile(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  handleSubmitCreateAccount = async evt => {
    evt.preventDefault();

    // create copy of state?
    const newAccount = {
      name: this.state.createAccountName,
      password: this.state.createAccountPassword,
      age: this.state.createAccountAge
    };

    console.log(newAccount);
    await this.sendPostRequestCreateProfile(newAccount);
    await this.props.setAuthed(
      true,
      this.state.createAccountName,
      this.state.createAccountPassword
    );
  };

  handleLogOut = evt => {
    evt.preventDefault();
    console.log(
      "Log out: " + this.state.loginName + " " + this.state.loginPassword
    );
    this.props.setAuthed(false, "");
  };

  render() {
    return (
      <div className={container()}>
        {this.props.isAuthed && (
          <div>
            <h1>Profil oplysninger</h1>
            <h2>Navn</h2>
            <p>
              {this.props.authedProfile.name && this.props.authedProfile.name}
            </p>
            <h2>Alder</h2>
            <p>
              {this.props.authedProfile.age && this.props.authedProfile.age}
            </p>
            <FormButton
              label="log ud"
              style={{ marginTop: "1rem" }}
              handleSubmit={this.handleLogOut}
            />
          </div>
        )}
        {!this.props.isAuthed && (
          <>
            <form>
              <h1>Log ind</h1>
              <h2>Brugernavn</h2>
              <TextInput
                name="loginName"
                value={this.state.loginName}
                handleChange={this.handleFormChange}
                initText="Brugernavn"
              />
              <h2>Kodeord</h2>
              <TextInput
                name="loginPassword"
                value={this.state.loginPassword}
                handleChange={this.handleFormChange}
                initText="Kodeord"
              />
              <FormButton
                label="log ind"
                style={{ marginTop: "1rem" }}
                handleSubmit={this.handleSubmitLogin}
              />
            </form>
            {!this.state.createAccount && (
              <div>
                <FormButton
                  label="Lav en ny profil"
                  style={{ marginTop: "1rem" }}
                  handleSubmit={() => {
                    this.setState({ createAccount: true });
                  }}
                />
              </div>
            )}
            {this.state.createAccount && (
              <form>
                <h1>Opret bruger</h1>
                <h2>Navn</h2>
                <TextInput
                  name="createAccountName"
                  value={this.state.createAccountName}
                  handleChange={this.handleFormChange}
                  initText="Navn"
                />
                <h2>Password</h2>
                <TextInput
                  name="createAccountPassword"
                  value={this.state.createAccountPassword}
                  handleChange={this.handleFormChange}
                  initText="Password"
                />
                <h2>Alder</h2>
                <TextInput
                  name="createAccountAge"
                  value={this.state.createAccountAge}
                  handleChange={this.handleFormChange}
                  initText="Alder"
                />

                <FormButton
                  handleSubmit={this.handleSubmitCreateAccount}
                  label="Send"
                  style={{ marginTop: "1rem" }}
                />
              </form>
            )}
          </>
        )}
      </div>
    );
  }
}

const Account = props => {
  const [addedProfile, setAddedProfile] = useState();
  const [loginName, setLoginName] = useState();
  const [loginPassword, setLoginPassword] = useState();
  const [name, setName] = useState();
  const [password, setPassword] = useState();
  const [age, setAge] = useState();

  const sendPostRequest = async profile => {
    const reqUrl = "http://localhost:5000/api/profiles";

    await axios
      .post(reqUrl, profile)
      .then(function(response) {
        setAddedProfile(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  const handleFormChange = evt => {
    console.log(useState([loginName]));
  };

  const handleChangeLoginName = e => {
    e.preventDefault();
    setName(e.target.value);
  };

  const handleChangeLoginPassword = e => {
    e.preventDefault();
    setPassword(e.target.value);
  };

  const handleLogin = e => {
    e.preventDefault();
    props.setAuthed(true, name, password);
    setName("");
    setPassword("");
  };

  const handleLogOut = () => {
    props.setAuthed(false, "");
  };

  const handleChangeCreateName = e => {
    e.preventDefault();
    setName(e.target.value);
  };

  const handleChangeCreatePassword = e => {
    e.preventDefault();
    setPassword(e.target.value);
  };

  const handleChangeCreateAge = e => {
    e.preventDefault();
    setAge(e.target.value);
  };

  const handleProfileSubmit = async e => {
    e.preventDefault();
    const thisProfile = { name, age };
    thisProfile.name = name;
    thisProfile.age = age;
    await sendPostRequest(thisProfile);
    await props.setAuthed(true, name, password);
  };

  return (
    <div className={container()}>
      {props.isAuthed && (
        <div>
          <h1>Profil oplysninger</h1>
