          <FormButton
            label="log ud"
            style={{ marginTop: "1rem" }}
            handleSubmit={handleLogOut}
          />
        </div>
      )}
      {!props.isAuthed && (
        <div>
          <h1>Log ind</h1>
          <form>
            <h2>Brugernavn</h2>
            <TextInput
              name="loginName"
              value={loginName}
              handleChange={handleFormChange}
              initText="Brugernavn"
            />
            <h2>Kodeord</h2>
            <TextInput
              name="loginPassword"
              value={loginPassword}
              handleChange={handleFormChange}
              initText="Kodeord"
            />
            <FormButton
