import React, { useState } from "react";
import axios from "axios";
import TextInput from "./ui/TextInput";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";

//css
import { css } from "emotion";

const Account = props => {
  const [addedProfile, setAddedProfile] = useState();
  const [profileName, setProfileName] = useState();
  const [name, setName] = useState();
  const [age, setAge] = useState();
  const [password, setPassword] = useState();

  const handleChangeName = e => {
    e.preventDefault();
    const thisName = e.target.value;
    setName(thisName);
  };

  const handleChangeAge = e => {
    e.preventDefault();
    const thisAge = e.target.value;
    setAge(e.target.value);
  };

  const handleChangePassword = e => {
    e.preventDefault();
    setPassword(e.target.value);
  };

  const handleSubmit = e => {
    e.preventDefault();
    const thisProfile = { ...profile };
    thisProfile.name = name;
    thisProfile.age = age;
    sendPostRequest(thisProfile);
  };

  const sendPostRequest = profile => {
    const reqUrl = "http://localhost:5000/api/profiles";

    axios
      .post(reqUrl, profile)
      .then(function(response) {
        setAddedProfile(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  const getPostRequest = () => {
    const reqUrl = "http://localhost:5000/api/profiles/";

    axios
      .get(reqUrl)
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  const handleLogin = e => {
    e.preventDefault();
    props.setAuthed(name);
  };

  return (
    <div className={container()}>
      {props.isAuthed && (
        <div>
          <h1>Profil oplysninger</h1>
          <h2>Navn</h2>
          <p>{props.authedProfile.name && props.authedProfile.name}</p>
          <h2>Alder</h2>
          <p>{props.authedProfile.age && props.authedProfile.age}</p>
        </div>
      )}
      {!props.isAuthed && (
        <div>
          <h1>Log ind</h1>
          <form>
            <h2>Brugernavn</h2>
            <TextInput handleChange={handleChangeName} initText="Brugernavn" />
            <h2>Kodeord</h2>
            <TextInput handleChange={handleChangePassword} initText="Kodeord" />
            <FormButton
              label="log ind"
              style={{ marginTop: "1rem" }}
              handleSubmit={handleLogin}
            />
          </form>
          <h1>Opret bruger</h1>
          <form>
            <h2>Navn</h2>
            <TextInput handleChange={handleChangeName} initText="Navn" />
            <h2>Alder</h2>
            <TextInput handleChange={handleChangeAge} initText="Alder" />
            <h2>Password</h2>
            <TextInput
              handleChange={handleChangePassword}
              initText="Password"
            />

            <FormButton
              handleSubmit={handleSubmit}
              label="Submit"
              style={{ marginTop: "1rem" }}
            />
          </form>
          <div className="list">
            {addedProfile && addedProfile.name && addedProfile.age && (
              <>
                <div>Hej {addedProfile.name}. Welkommen til forumet</div>
                <h2>Dine profiloplysninger: </h2>
                <div>Navn: {addedProfile.name}</div>
                <div>Age: {addedProfile.age}</div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

const container = () => css`
  .list {
    margin-top: 1rem;

    .message {
      padding-bottom: 1rem;
    }
  }

  h1 {
    font-size: 1.5rem;
  }

  h2 {
    font-size: 1rem;
  }
`;

export default Account;
