import React, { useState } from "react";
import axios from "axios";
import TextInput from "./ui/TextInput";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";

//css
import { css } from "emotion";

const Account = () => {
  const [profile, setProfile] = useState({
    name: "Jens",
    age: 37
  });
  const [addedProfile, setAddedProfile] = useState();
  const [profileName, setProfileName] = useState();
  const [name, setName] = useState();
  const [password, setPassword] = useState();

  const handleChangeName = e => {
    e.preventDefault();
    setName(e.target.value);
  };

  const handleChangePassword = e => {
    e.preventDefault();
    setPassword(e.target.value);
  };

  const handleSubmit = e => {
    e.preventDefault();
    const profile = { ...profile };

    sendPostRequest(profile);
  };

  const sendPostRequest = profile => {
    const reqUrl = "http://localhost:5000/api/profiles";

    axios
      .post(reqUrl, profile)
      .then(function(response) {
        setAddedProfile(response.data);
        console.log(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  const getPostRequest = () => {
    const reqUrl = "http://localhost:5000/api/profiles/";

    axios
      .get(reqUrl)
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  return (
    <div className={container()}>
      <form>
        <h2>Profile</h2>
        <TextInput
          handleChange={handleChangeName}
          initText="Type your message"
        />
        {/*<h2>Password</h2>
        <TextInput
          handleChange={handleChangePassword}
          initText="Type your password"
        />
        <h2>Profile picture</h2>
        <FileInput />*/}
        <FormButton
          handleSubmit={handleSubmit}
          label="Submit"
          style={{ marginTop: "1rem" }}
        />
      </form>
      <div className="list">
        {addedProfile && `Hej ${addedProfile.name}. Welkommen til forumet`}
      </div>
    </div>
  );
};

const container = () => css`
  .list {
    margin-top: 1rem;

    .message {
      padding-bottom: 1rem;
    }
  }
`;

export default Account;
