      <textarea
        rows="4"
        onChange={props.handleChange}
        placeholder={props.initText}
        value={value}
      />
    </div>
  );
};

const container = () => css`
  textarea {
    border: 1px solid gray;
    width: 100%;
    padding: 1rem;
  }
`;

export default TextArea;
