import React, { useState, useEffect } from "react";
// css
import { AccountIcon, ErrorIcon } from "mdi-react";
// components 
import Warning from "./ui/Warning";
import TextInput from "./ui/TextInput";
import FormButton from "./ui/FormButton";

const Login = props => {
    // const [name, setName] = useState();
    // const [password, setPassword] = useState();
    const [submitted, setSubmitted] = useState(false);
    // const [validForm, setValidForm] = useState(false);
    
    // useEffect(() => console.log("Name: " + name), [name]);
    // useEffect(() => console.log("Password: " + password), [password]);
    // useEffect(() => console.log("Valid form: " + validForm), [validForm]);

    // const handleFormChange = e => {
    //     e.target.name === "name" && setName(e.target.value);
    //     e.target.name === "password" && setPassword(e.target.value);
    // }

    // const handleSubmitLogin = e => {
    //     e.preventDefault();
    //     setSubmitted(true);
    //     if (name && password) setValidForm(true)
    
    //     if (validForm) {
    //         props.handleLogin(true, name, password);
    //     };
    // }

    return <>
       <form>
            <h1>Log ind</h1>
            <h2>Brugernavn</h2>
            <TextInput
                name={"loginName"}
                value={props.name}
                handleChange={props.handleFormChange}
                initText="Brugernavn"/>
            {!props.name && submitted && <Warning validationText={"Navn mangler at blive udfyldt"}/>}
            <h2>Kodeord</h2>
            <TextInput
                name="loginPassword"
                value={props.password}
                handleChange={props.handleFormChange}
                initText="Kodeord"/>
            {!props.password && submitted && <Warning validationText={"Password mangler at blive udfyldt"}/>}
            <FormButton
                label="log ind"
                iconLeft={<AccountIcon/>}
                style={{ marginTop: "1rem" }}
                handleSubmit={e => {
                    props.handleSubmit(e);
                    setSubmitted(true);
                }}/>
            {props.errorMessageAuthentication && (
            <p className="error-message">
                <ErrorIcon />
                {props.errorMessageAuthentication}
                {props.unsetErrorMessageLogIn()}
            </p>
            )}
        </form>
    </> 
}

export default Login;
