import React, { useState, useEffect } from "react";
// css
import { AccountIcon, ErrorIcon } from "mdi-react";
// components 
import Warning from "./ui/Warning";
import TextInput from "./ui/TextInput";
import TextArea from "./ui/TextArea";
import FormButton from "./ui/FormButton";

const Login = props => {
    const [loginName, setLoginName] = useState();
    const [loginPassword, setLoginPassword] = useState();
    const [valName, setValName] = useState();
    const [valPassword, setValPassword] = useState();
   
    const handleFormChange = e => {
        e.target.name === "name" && setLoginName(e.target.value);
        e.target.name === "password" && setLoginPassword(e.target.value);
    }

    const handleSubmitLogin = e => {
        e.preventDefault();
        clearAllValidations();
    }

    const clearAllValidations = () => {
        setValName();
        setValPassword();
    }

    return <>
       <form>
            <h1>Log ind</h1>
            <h2>Brugernavn</h2>
            <TextInput
            name="loginName"
            value={loginName}
            handleChange={handleFormChange}
            initText="Brugernavn"
            />
            <Warning validationText={valName}/>              
            <h2>Kodeord</h2>
            <TextInput
            name="loginPassword"
            value={loginPassword}
            handleChange={handleFormChange}
            initText="Kodeord"
            />
            <Warning validationText={valPassword}/>
            <FormButton
            label="log ind"
            iconLeft={<AccountIcon/>}
            style={{ marginTop: "1rem" }}
            handleSubmit={handleSubmitLogin}
            />

            {props.errorMessageAuthentication && (
            <p className="error-message">
                <ErrorIcon />
                {props.errorMessageAuthentication}
                {props.unsetErrorMessageLogIn()}
            </p>
            )}
        </form>
    </> 
}

export default Login;
