import React, { useState, useEffect } from "react";
// router
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";
// css
import { css } from "emotion";
// components
import axios from "axios";
import Header from "./components/Header";
import Main from "./components/Main";
import MessageBoard from "./components/MessageBoard";
import Home from "./components/Home";
import Profile from "./components/Profile";
import Account from "./components/Account";

//{}
//[]
//||

// version 1.
// Form validation
// Storage of encrypted passwords
// Secure storage of user data in localstorage
// cors policy
// profileimages. Can be a svg temporarily
// Github repository

// Version 2.
// Implement redux as state managaer

// Bug ved refresh, når man ikke er logget ind -> cleary

const App = () => {
  const initIsAuthed = () => localStorage.getItem("isAuthed") || false;
  let initAuthedAccount = JSON.parse(localStorage.getItem("account")) || {};
  const [isAuthed, setIsAuthed] = useState(initIsAuthed);
  const [authedProfile, setAuthedProfile] = useState(initAuthedAccount);
  const [authed, setAuthed] = useState({
    isAuthed: false,
    profileName: undefined
  });

  useEffect(() => {
    localStorage.setItem("isAuthed", isAuthed);
    localStorage.setItem("account", JSON.stringify(authedProfile));
  }, [isAuthed, authedProfile]);

  useEffect(() => {
    localStorage.setItem("AuthedisAuthed", authed.isAuthed);
    localStorage.setItem("AuthedProfileName", authed.profileName);
  }, [authed]);

  const handleAuthentication = async (validLogin, userName, password) => {
    try {
      if (validLogin === false) {
        setAuthed({ isAuthed: false, authedProfile: undefined });
        setIsAuthed(false);
        setAuthedProfile({});
      } else if (validLogin === true && userName && password) {
        const reqUrl = `http://localhost:5000/api/auth/${userName}`;
        let { data } = await axios.get(reqUrl);
        setAuthed({ isAuthed: true, profileName: data.name });
        setAuthedProfile(data);
        setIsAuthed(true);
      }
    } catch {}
  };

  return (
    <Router>
      <div className={container()}>
        {console.log(localStorage.getItem("AuthedProfileName") || undefined)}
        <Header authedProfile={authedProfile} />
        <Main className="main">
          <Switch>
            <Route
              path="/account"
              render={props => (
                <Account
                  {...props}
                  isAuthed={isAuthed}
                  authedProfile={authedProfile}
                  setAuthed={handleAuthentication}
                />
              )}
            />
            <Route path="/profile/:id" component={Profile} />
            <Route
              path="/messages"
              render={props => (
                <MessageBoard
                  {...props}
                  isAuthed={isAuthed}
                  authedProfile={authedProfile}
                />
              )}
            />
            <Route exact path="/frontpage" component={Home} />
            <Route path="/profile/:id" component={Profile} />
            <Route path="/" render={() => <Redirect to="/frontpage" />} />
          </Switch>
        </Main>
      </div>
    </Router>
  );
};

const container = () => css`
  header {
    display: flex;

    div {
      padding: 1rem;
    }

    span {
      vertical-align: super;
    }

    svg {
      padding-right: 1rem;
    }
  }
`;

export default App;
