import React, { useState } from "react";
// router
import {
  BrowserRouter as Router,
  Route,
  Link,
  Redirect,
  Switch
} from "react-router-dom";
// css
import { css } from "emotion";
// material deasign icons
import { HomeIcon, AccountIcon, MessageIcon } from "mdi-react";
// components
import axios from "axios";
import Header from "./components/Header";
import Main from "./components/Main";
import MessageBoard from "./components/MessageBoard";
import Home from "./components/Home";
import Profile from "./components/Profile";
import Account from "./components/Account";

const App = () => {
  const [isAuthed, setIsAuthed] = useState(false);
  const [authedProfileId, setAuthedProfileId] = useState(1);
  const [authedprofile, setAuthedProfile] = useState({});

  const handleAthentication = profileId => {
    console.log("I can handle authentication: " + profileId);
    getProfileRequest(profileId);
  };

  const getProfileRequest = id => {
    const reqUrl = `http://localhost:5000/api/profiles/${id}`;

    axios
      .get(reqUrl)
      .then(function(response) {
        console.log(response.data);
        setIsAuthed(true);
        setAuthedProfile(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  return (
    <Router>
      <div className={container()}>
        <header>
          <div>
            <Link to="/">
              <HomeIcon />
              <span>Home</span>
            </Link>
          </div>
          <div>
            <Link to="/account">
              <AccountIcon />
              <span>Account</span>
            </Link>
          </div>
          <div>
            <Link to="/messages">
              <MessageIcon />
              <span>Messages</span>
            </Link>
          </div>
        </header>
        <Main className="main">
          <Switch>
            <Route
              path="/account"
              render={props => (
                <Account
                  {...props}
                  isAuthed={isAuthed}
                  authedProfileId={authedProfileId}
                  handleAthentication={handleAthentication}
                />
              )}
            />
            <Route path="/profile/:id" component={Profile} />
            <Route path="/messages" component={MessageBoard} />
            <Route exact path="/frontpage" component={Home} />
            <Route path="/profile/:id" component={Profile} />
            <Route path="/" render={() => <Redirect to="/frontpage" />} />
          </Switch>
        </Main>
      </div>
    </Router>
  );
};

const container = () => css`
  header {
    display: flex;

    div {
      padding: 1rem;
    }

    span {
      vertical-align: super;
    }

    svg {
      padding-right: 1rem;
    }
  }
`;

export default App;
