import React, { Component } from "react";
import TextArea from "./ui/TextArea";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";

//css
import { css } from "emotion";

class MessageBoard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messages: {},
      messgage: {}
    };
  }

  async getMessages() {
    const urlParams = window.location.href.split("profile/:");
    const id = urlParams[1];
    const apiUrl = `http://localhost:5000/api/profiles/${id}`;

    await fetch(apiUrl)
      .then(response => {
        return response.json();
      })
      .then(data => {
        const profile = data;
        this.setState({ profile: profile });
      });
  }

  handleChange = e => {
    e.preventDefault();
    this.setState({ message: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();
    this.setState({ messages: [this.state.message, ...this.state.messages] });
  };

  render() {
    return (
      <div className={container()}>
        <form>
          <h2>Message</h2>
          <TextArea
            initText="Type your message"
            handleChange={this.handleChange}
          />
          <h2>Upload file</h2>
          <FileInput />
          <FormButton
            label="submit"
            style={{ marginTop: "1rem" }}
            handleSubmit={this.handleSubmit}
          />
        </form>
        <div className="list">
          {messages.map((message, index) => (
            <div className="message" key={index}>
              {message}
            </div>
          ))}
        </div>
      </div>
    );
  }
}

const container = () => css`
  .list {
    margin-top: 1rem;

    .message {
      padding-bottom: 1rem;
    }
  }
`;

export default MessageBoard;
