import React, { Component } from "react";
import TextArea from "./ui/TextArea";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";
import axios from "axios";

//css
import { css } from "emotion";
import { LetterAIcon, SendIcon } from "mdi-react";

//{}

class MessageBoard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      post: {},
      profileName: "",
      message: ""
    };
  }

  componentDidMount() {
    this.getPostsRequest();
  }

  handleFormChange = e => {
    e.preventDefault();
    this.setState({ message: e.target.value });
  };

  handlePostSubmit = e => {
    e.preventDefault();
    const { valTxtMessage, message, authed } = this.state;
    this.SetState({ valTxtMessage: "" });
    this.state.message === "" &&
      this.SetState({ valTxtMessage: "Besked skal vÃ¦re udfyldt" });

    // lav en post
    const thisMessage = this.state.message;
    const profileName = this.props.authed.profileName;
    const post = { profileName: profileName, message: thisMessage };

    this.sendPostRequest(post);
  };

  getPostsRequest = async () => {
    const reqUrl = `${this.props.baseUrl}/posts`;

    await axios
      .get(reqUrl)
      .then(
        function(response) {
          this.setState({ posts: response.data });
        }.bind(this) // In axios this refers to axios not the class. Solved with bind(this)
      )
      .catch(function(error) {
        console.log(error);
      });
  };

  sendPostRequest = async post => {
    const reqUrl = `${this.props.baseUrl}/post`;

    await axios
      .post(reqUrl, post)
      .then(
        function(response) {
          this.setState({ posts: response.data });
          this.setState({ message: "" });
        }.bind(this) // In axios this refers to axios not the class. Solved with bind(this)
      )
      .catch(function(error) {
        console.log(error);
      });
  };

  render() {
    return (
      <>
        <div className={container()}>
          <form>
            <h2>Skriv en besked</h2>
            <TextArea
              initText="Type your message"
              handleChange={this.handleFormChange}
              message={this.state.message}
            />
            <FormButton
              label={
                <>
                  <SendIcon /> Send
                </>
              }
              style={{ marginTop: "1rem" }}
              handleSubmit={e => this.handlePostSubmit(e)}
            />
          </form>
          <div>{this.state.valTxtMessage && this.state.valTxtMessage}</div>
          <div className="list">
            {this.state.posts.length > 0 &&
              [...this.state.posts].reverse().map((obj, index) => (
                <div key={index} style={{ marginTop: "1rem" }}>
                  <div>
                    {(obj.profileName && obj.profileName) || "Ukendt bruger"}
                  </div>
                  <div>{obj.dateTime}</div>
                  <div>{obj.message}</div>
                </div>
              ))}
          </div>
        </div>
      </>
    );
  }
}

const container = () => css`
  .list {
    margin-top: 1rem;

    .message {
      padding-bottom: 1rem;
    }
  }
`;

export default MessageBoard;
