import React, { Component } from "react";
import { Link } from "react-router-dom";

//css
import { css } from "emotion";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profile: {}
    };
  }

  componentDidMount() {
    this.getProfile();
  }

  async getProfile() {
    const urlParams = window.location.href.split("profile/:");
    const id = urlParams[1];
    const apiUrl = `http://localhost:5000/api/boys/${id}`;

    await fetch(apiUrl)
      .then(response => {
        return response.json();
      })
      .then(data => {
        const profile = data;
        this.setState({ profil: profile });
      });
  }

  render() {
    const { profile } = this.state;

    return (
      <div className={container()}>
        <div className="cart">
          <h2>{profile.name}</h2>
          <h2>{profile.age} Ã¥r</h2>
        </div>
        <div>
          <img
            className="image"
            src={process.env.PUBLIC_URL + `/profileImages/${profile.image}.jpg`}
          />
        </div>
        <div>{profile.description}</div>
        <Link to={`/`}>
          <button>tilbage</button>
        </Link>
      </div>
    );
  }
}

const container = () => css`
  .cart {
    display: flex;
    justify-content: space-between;
    max-width: 1000;
  }

  button {
    cursor: pointer;
    width: 100%;
    padding: 0.5rem;
    margin: 0.5rem 0 0.5rem 0;
    border: 1px solid gray;
    background-color: white;

    span {
      font-size: 1rem;
      text-align: top;
    }

    svg {
      padding-right: 1rem;
    }
  }
`;

export default Profile;
