import React, { Component } from "react";
import TextArea from "./ui/TextArea";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";
import axios from "axios";

//css
import { css } from "emotion";

class MessageBoard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      post: {},
      message: ""
    };
  }

  componentDidMount() {
    this.getPosts();
  }

  getPosts = () => {
    const reqUrl = "http://localhost:5000/api/posts";

    axios
      .get(reqUrl)
      .then(
        function(response) {
          console.log("Get posts ->" + response.data);
          this.setState({ posts: response.data });
        }.bind(this) // In axios this refers to axios not the class. Solved with bind(this)
      )
      .catch(function(error) {
        console.log(error);
      });
  };

  postPosts = post => {
    const reqUrl = "http://localhost:5000/api/posts";

    axios
      .post(reqUrl, post)
      .then(
        function(response) {
          console.log("Response: " + response.data);
        }.bind(this) // In axios this refers to axios not the class. Solved with bind(this)
      )

      .catch(function(error) {
        console.log(error);
      });
  };

  handleChange = e => {
    e.preventDefault();
    this.setState({ message: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();
    // lav en post
    const message = this.state.message;
    const accountId = 2;
    this.setState({ post: accountId, message });
    // Send post to API
    // if it is not empty ...
    //this.postPosts(this.state.post);
  };

  render() {
    return (
      <>
        <div className={container()}>
          <form>
            <h2>Message</h2>
            <TextArea
              initText="Type your message"
              handleChange={this.handleChange}
            />
            {/*h2>Upload file</h2>
          <FileInput />*/}
            <FormButton
              label="submit"
              style={{ marginTop: "1rem" }}
              handleSubmit={this.handleSubmit}
            />
          </form>
          <div className="list">
            {this.state.posts.length > 0 &&
              this.state.posts.map(obj => obj.message)}
          </div>
        </div>
      </>
    );
  }
}

const container = () => css`
  .list {
    margin-top: 1rem;

    .message {
      padding-bottom: 1rem;
    }
  }
`;

export default MessageBoard;
