import React, { useState, useEffect } from "react";
// router
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch
} from "react-router-dom";
// css
import { css } from "emotion";
// components
import axios from "axios";
import Header from "./components/Header";
import Main from "./components/Main";
import MessageBoard from "./components/MessageBoard";
import Home from "./components/Home";
import Profile from "./components/Profile";
import Account from "./components/Account";

//{}
//[]
//||

const App = () => {
  const initIsAuthed = () => localStorage.getItem("isAuthed") || false;
  const initAuthedAccount = () => localStorage.getItem("account") || {};
  const [isAuthed, setIsAuthed] = useState(initIsAuthed);
  const [authedProfile, setAuthedProfile] = useState(initAuthedAccount);

  /*useEffect(() => {
    console.log("Did mount");
    const localIsAuthed = localStorage.getItem(isAuthed);
    console.log(localIsAuthed);
  }, []);*/

  /*useEffect(() => {
    return () => {
      console.log("Clean up - will unmoiunt");
      localStorage.setItem("isAuthed", isAuthed);
      localStorage.setItem("accountName", authedProfile);
      console.log(
        "onUpdate: " + isAuthed + " " + JSON.stringify(authedProfile)
      );
    };
  });*/

  useEffect(() => {
    console.log("Add to localstorgatge");
    localStorage.setItem("isAuthed", isAuthed);
    localStorage.setItem("account", authedProfile);
    console.log("onUpdate: " + isAuthed + " " + authedProfile);
  }, [isAuthed, authedProfile]);

  const setAuthed = async (login, userName, password) => {
    if (login === false) {
      setIsAuthed(false);
      setAuthedProfile({});
    } else if (login === true && userName && password) {
      const reqUrl = `http://localhost:5000/api/auth/${userName}`;
      let res = await axios.get(reqUrl);
      console.log(res.data);
      setAuthedProfile(res.data);
      setIsAuthed(true);
    }
  };

  const getProfileRequest = async username => {
    const reqUrl = `http://localhost:5000/api/auth/${username}`;

    axios
      .get(reqUrl)
      .then(function(response) {
        console.log(response.data);
        setIsAuthed(true);
        setAuthedProfile(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  return (
    <Router>
      <div className={container()}>
        {console.log(initAuthedAccount)}
        <Header authedProfile={authedProfile} />
        <Main className="main">
          <Switch>
            <Route
              path="/account"
              render={props => (
                <Account
                  {...props}
                  isAuthed={isAuthed}
                  authedProfile={authedProfile}
                  setAuthed={setAuthed}
                />
              )}
            />
            <Route path="/profile/:id" component={Profile} />
            {/*<Route path="/messages" component={MessageBoard} />*/}
            <Route
              path="/messages"
              render={props => (
                <MessageBoard
                  {...props}
                  isAuthed={isAuthed}
                  authedProfile={authedProfile}
                />
              )}
            />
            <Route exact path="/frontpage" component={Home} />
            <Route path="/profile/:id" component={Profile} />
            <Route path="/" render={() => <Redirect to="/frontpage" />} />
          </Switch>
        </Main>
      </div>
    </Router>
  );
};

const container = () => css`
  header {
    display: flex;

    div {
      padding: 1rem;
    }

    span {
      vertical-align: super;
    }

    svg {
      padding-right: 1rem;
    }
  }
`;

export default App;
