import React, { Component } from "react";
import { AccountIcon } from "mdi-react";
import { css } from "emotion";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      boys: {},
      boy: undefined
    };
  }

  componentDidMount() {
    this.getBoys();
    this.getBoy(1);
  }

  async getBoys() {
    const apiUrl = `http://localhost:5000/api/boys/`;

    await fetch(apiUrl)
      .then(response => {
        return response.json();
      })
      .then(data => {
        const boys = data;
        this.setState({ boys: boys });
        console.log(boys);
      });
  }

  async getBoy(id) {
    const apiUrl = `http://localhost:5000/api/boys/${id}`;

    await fetch(apiUrl)
      .then(response => {
        return response.json();
      })
      .then(data => {
        const boy = data;
        console.log(boy);
      });
  }

  render() {
    const { boys } = this.state;

    return (
      <div className={container()}>
        <h1>Se mig nøgen</h1>
        <p />
        <p>
          En ny undersøgelse viser at drenge i 7 klasse er mere
          kropsforskrækkede end tidligere.
        </p>
        <p>
          Se-mig-nøgen er et program som har til formål at gøre drenge i 7 B
          mere fortrolige med deres kroppe. Drengene fik i seksualundervisning
          til opgave at lave en pose og en profil. Af den opgave udsprang sitet
          Se mig nøgen.
        </p>
        <p>
          Drengene kan en profil, med billede og tekst, og andre kan studere
          billederne og sende kommentarer. Drengene kan også kontakte de andre
          drengene direkte.
        </p>
        <div
          style={{
            width: "100%",
            display: "flex",
            flexWrap: "wrap",
            border: "1px solid gray"
          }}
        >
          {boys.length > 0 &&
            boys.map((obj, index) => (
              <div key={index} className="card">
                <div className="card-top">
                  <h2>{obj.name}</h2>
                  <h2>{`${obj.age} år`}</h2>
                </div>
                <div>
                  <img
                    className="image"
                    src={
                      process.env.PUBLIC_URL + `/profileImages/${obj.image}.jpg`
                    }
                  />
                </div>
                <div className="description">{obj.description}</div>
                <button
                  className="button"
                  onClick={() => {
                    console.log("See my profile");
                  }}
                >
                  <AccountIcon />
                  <span>Se min profil</span>
                </button>
              </div>
            ))}
        </div>
      </div>
    );
  }
}

const container = () => css`
  .card {
    padding: 0 1rem 1.5rem 1rem;
    width: 100%;

    .card-top {
      display: flex;
      justify-content: space-between;
      padding-right: 1rem;
    }

    button {
      cursor: pointer;
      width: 100%;
      padding: 0.5rem;
      margin: 0.5rem 0 0.5rem 0;

      span {
        font-size: 1rem;
        text-align: top;
      }

      svg {
        padding-right: 1rem;
      }
    }

    .description {
      padding: 0.5rem 0 0.5rem 0;
    }

    img {
      padding: 0.5rem 0 0.5rem 0;
      width: 100%;
      max-width: 1000px;
    }
  }
`;

export default Home;
