import React, { useState } from "react";
// css
import { css } from "emotion";

const TextInput = props => {
  return (
    <div className={container()}>
      <p className="error-message">
        <ErrorIcon />
        {validationText}
      </p>
    </div>
  );
};

const container = () => css`
  input {
    border: 1px solid gray;
    padding: 1rem;
    width: 100%;
    box-sizing: border-box;
  }
`;

export default TextInput;
