import React, { useState, useEffect } from "react";
// css
import { CancelIcon, SendIcon, ErrorIcon } from "mdi-react";
// components 
import Warning from "./ui/Warning";
import TextInput from "./ui/TextInput";
import TextArea from "./ui/TextArea";
import FormButton from "./ui/FormButton";

const Login = props => {
    const [loginName, setLoginName] = userState();
    const [loginPassword, setLoginPassword] = userState();
    const [valTxtName, setValTxtName] = userState();
    const [valTxtPassword, setValTxtPassword] = userState();
   
    return <>
       <form>
            <h1>Log ind</h1>
            <h2>Brugernavn</h2>
            <TextInput
            name="loginName"
            value={loginName}
            handleChange={this.handleFormChange}
            initText="Brugernavn"
            />
            <Warning validationText={valTxtName}/>              
            <h2>Kodeord</h2>
            <TextInput
            name="loginPassword"
            value={loginPassword}
            handleChange={this.handleFormChange}
            initText="Kodeord"
            />
            <Warning validationText={valTxtPassword}/>
            <FormButton
            label="log ind"
            iconLeft={<AccountIcon/>}
            style={{ marginTop: "1rem" }}
            handleSubmit={this.handleSubmitLogin}
            />

            {this.props.errorMessageAuthentication && (
            <p className="error-message">
                <ErrorIcon />
                {this.props.errorMessageAuthentication}
                {this.props.unsetErrorMessageLogIn()}
            </p>
            )}
        </form>
    </> 
}

export default Login;
