import React, { useState, useEffect } from "react";
// css
import { css } from "emotion";
import { CancelIcon, SendIcon } from "mdi-react";
// components 
import Warning from "./ui/Warning";
import TextInput from "./ui/TextInput";
import TextArea from "./ui/TextArea";
import FormButton from "./ui/FormButton";

const EditAccount = props => {
    const {account} = props;

    const [valTextName, setTextName] = useState("Navn mangler at blive udfyldt");
    const [valTextPassword, setTextPassword] = useState("Adgagnskode mangler at blive udfyldt");
    const [valTextAge, setTextAge] = useState("Alder mangler at blive udfyldt");
    const [valTextOccupation, setTextOccupation] = useState("Besk√¶ftigelse mangler at blive udfyldt");
    const [valTextRegion, setTextRegion] = useState("Region mangler at blive udfyldt");
    const [valTextText, settextText] = useState("Profiltekst mangler at blive udfyldt");

    const [name, setName] = useState();
    const [password, setPassword] = useState();
    const [age, setAge] = useState();
    const [occupation, setOccupation] = useState();
    const [region, setRegion] = useState();
    const [text, setText] = useState();


    useEffect (() => {
        console.log(console.log(props.account))
    },[])
    
    const handleFormChange = (e) => {
        console.log(e.target.name)
        e.target.name === name && setName(e.target.value)
    }

    const handleUpdateAccount = () => {

    }

    const clearAllValidations = () => {

    }

    return <>
    <h1>Rediger brugeroplysninger</h1>
    <h2>Brugernavn</h2>
    <TextInput
      name="name"
      defaultValue={account.name}
      handleChange={handleFormChange}
    />
    <Warning validationText={valTextName}/>
    <h2>Kodeord</h2>
    <TextInput
      name="password"
      dafaultValue={account.password}
      handleChange={handleFormChange}
    />
    <Warning validationText={valTextPassword}/>
    <h2>Alder</h2>
    <TextInput
      name="age"
      defaultValue={account.age}
      handleChange={handleFormChange}
    />
    <Warning validationText={valTextAge}/>
    <h2>Besk√¶ftigelse</h2>
    <TextInput
      name="occupation"
      defaultValue={account.occupation}
      handleChange={handleFormChange}
    />
    <Warning validationText={valTextOccupation}/>
    <h2>Region</h2>
    <TextInput
      name="region"
      defaultValue={account.region}
      handleChange={handleFormChange}
    />
    <Warning validationText={valTextRegion}/>              
    <h2>Profiltekst</h2>
    <TextArea 
      rows={20}
      name="text" 
      defaultValue={account.text}
      handleChange={handleFormChange} 
    />
    <Warning validationText={valTextText}/>
    <FormButton
      label="Gem"
      iconLeft={<SendIcon/>}
      style={{ marginTop: "1rem" }}
      handleSubmit={handleUpdateAccount}
    />
    <FormButton
      label="Annuller"
      iconLeft={<CancelIcon/>}
      style={{ marginTop: "1rem" }}
      handleSubmit={() => {this.setState({editAccount: false}, () => localStorage.removeItem("editAccount"));this.clearAllErrorMessages()}}
    />
  </> 
}

export default EditAccount;
