import React, { useState } from "react";
import axios from "axios";
import TextInput from "./ui/TextInput";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";

//css
import { css } from "emotion";

const Account = props => {
  const [addedProfile, setAddedProfile] = useState();
  const [loginName, setLoginName] = useState();
  const [loginPassword, setLoginPassword] = useState();
  const [name, setName] = useState();
  const [password, setPassword] = useState();
  const [age, setAge] = useState();

  const sendPostRequest = async profile => {
    const reqUrl = "http://localhost:5000/api/profiles";

    await axios
      .post(reqUrl, profile)
      .then(function(response) {
        setAddedProfile(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  const handleFormChange = evt => {
    console.log(evt.target.name + " " + evt.taget.value);
  };

  const handleChangeLoginName = e => {
    e.preventDefault();
    setName(e.target.value);
  };

  const handleChangeLoginPassword = e => {
    e.preventDefault();
    setPassword(e.target.value);
  };

  const handleLogin = e => {
    e.preventDefault();
    props.setAuthed(true, name, password);
    setName("");
    setPassword("");
  };

  const handleLogOut = () => {
    props.setAuthed(false, "");
  };

  const handleChangeCreateName = e => {
    e.preventDefault();
    setName(e.target.value);
  };

  const handleChangeCreatePassword = e => {
    e.preventDefault();
    setPassword(e.target.value);
  };

  const handleChangeCreateAge = e => {
    e.preventDefault();
    setAge(e.target.value);
  };

  const handleProfileSubmit = async e => {
    e.preventDefault();
    const thisProfile = { name, age };
    thisProfile.name = name;
    thisProfile.age = age;
    await sendPostRequest(thisProfile);
    await props.setAuthed(true, name, password);
  };

  return (
    <div className={container()}>
      {props.isAuthed && (
        <div>
          <h1>Profil oplysninger</h1>
          <h2>Navn</h2>
          <p>{props.authedProfile.name && props.authedProfile.name}</p>
          <h2>Alder</h2>
          <p>{props.authedProfile.age && props.authedProfile.age}</p>
          <FormButton
            label="log ud"
            style={{ marginTop: "1rem" }}
            handleSubmit={handleLogOut}
          />
        </div>
      )}
      {!props.isAuthed && (
        <div>
          <h1>Log ind</h1>
          <form>
            <h2>Brugernavn</h2>
            <TextInput
              name="loginName"
              value={loginName}
              handleChange={handleFormChange}
              initText="Brugernavn"
            />
            <h2>Kodeord</h2>
            <TextInput
              name="loginPassword"
              value={loginPassword}
              handleChange={handleFormChange}
              initText="Kodeord"
            />
            <FormButton
              label="log ind"
              style={{ marginTop: "1rem" }}
              handleSubmit={handleLogin}
            />
          </form>
          <h1>Opret bruger</h1>
          <form>
            <h2>Navn</h2>
            <TextInput handleChange={handleChangeCreateName} initText="Navn" />
            <h2>Alder</h2>
            <TextInput handleChange={handleChangeCreateAge} initText="Alder" />
            <h2>Password</h2>
            <TextInput
              handleChange={handleChangeCreatePassword}
              initText="Password"
            />

            <FormButton
              handleSubmit={handleProfileSubmit}
              label="Submit"
              style={{ marginTop: "1rem" }}
            />
          </form>
          <div className="list">
            {addedProfile && addedProfile.name && addedProfile.age && (
              <>
                <div>Hej {addedProfile.name}. Welkommen til forumet</div>
                <h2>Dine profiloplysninger: </h2>
                <div>Navn: {addedProfile.name}</div>
                <div>Age: {addedProfile.age}</div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

const container = () => css`
  .list {
    margin-top: 1rem;

    .message {
      padding-bottom: 1rem;
    }
  }

  h1 {
    font-size: 1.5rem;
  }

  h2 {
    font-size: 1rem;
  }
`;

export default Account;
