import React, { Component } from "react";
import { WrapIcon } from "mdi-react";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      boys: {},
      boy: undefined
    };
  }

  componentDidMount() {
    this.getBoys();
    this.getBoy(1);
  }

  async getBoys() {
    const apiUrl = `http://localhost:5000/api/boys/`;

    await fetch(apiUrl)
      .then(response => {
        return response.json();
      })
      .then(data => {
        const boys = data;
        this.setState({ boys: boys });
        console.log(boys);
      });
  }

  async getBoy(id) {
    const apiUrl = `http://localhost:5000/api/boys/${id}`;

    await fetch(apiUrl)
      .then(response => {
        return response.json();
      })
      .then(data => {
        const boy = data;
        console.log(boy);
      });
  }

  render() {
    const { boys } = this.state;

    return (
      <>
        <h1>Se mig nøgen</h1>
        <p />
        <p>
          En ny undersøgelse viser at drenge i 7 klasse er mere
          kropsforskrækkede end tidligere.
        </p>
        <p>
          Se-mig-nøgen er et program som har til formål at gøre drenge i 7 B
          mere fortrolige med deres kroppe. Drengene fik i seksualundervisning
          til opgave at lave en pose og en profil. Af den opgave udsprang sitet
          Se mig nøgen.
        </p>
        <p>
          Drengene kan en profil, med billede og tekst, og andre kan studere
          billederne og sende kommentarer. Drengene kan også kontakte de andre
          drengene direkte.
        </p>
        <div
          style={{
            width: "100%",
            display: "flex",
            flexWrap: "wrap",
            border: "1px solid gray"
          }}
        >
          {console.log("Boys: ")}
          {boys.length > 0 && boys.map(obj => <div>obj.name</div>)}
        </div>
      </>
    );
  }
}

export default Home;
