import React, { Component } from "react";
import TextArea from "./ui/TextArea";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";
import axios from "axios";

//css
import { css } from "emotion";

class MessageBoard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      post: {},
      profileName: "",
      message: ""
    };
  }

  componentDidMount() {
    this.getPosts();
    //this.getProfileId();
  }

  /*
  getProfileId = () => {
    console.log("Get profile Id");
    this.setState({ profileId: 3 });
  };*/

  getPosts = async () => {
    const reqUrl = "http://localhost:5000/api/posts";

    await axios
      .get(reqUrl)
      .then(
        function(response) {
          this.setState({ posts: response.data });
        }.bind(this) // In axios this refers to axios not the class. Solved with bind(this)
      )
      .catch(function(error) {
        console.log(error);
      });
  };

  handlePostChange = e => {
    e.preventDefault();
    this.setState({ message: e.target.value });
  };

  handlePostSubmit = e => {
    e.preventDefault();
    // lav en post
    const message = this.state.message;
    const profileName = this.props.authedProfile.name;
    const post = { profileName: profileName, message: message };

    this.sendPostRequest(post);
  };

  sendPostRequest = async post => {
    const reqUrl = "http://localhost:5000/api/post";

    await axios
      .post(reqUrl, post)
      .then(
        function(response) {
          this.setState({ posts: response.data });
          this.setState({ message: "" });
        }.bind(this) // In axios this refers to axios not the class. Solved with bind(this)
      )
      .catch(function(error) {
        console.log(error);
      });
  };

  render() {
    return (
      <>
        <div className={container()}>
          <form>
            <h2>Message</h2>
            <TextArea
              initText="Type your message"
              handleChange={this.handlePostChange}
              message={this.state.message}
            />
            {/*h2>Upload file</h2>
          <FileInput />*/}
            <FormButton
              label="submit"
              style={{ marginTop: "1rem" }}
              handleSubmit={e => this.handlePostSubmit(e)}
            />
          </form>
          <div className="list">
            {this.state.posts.length > 0 &&
              [...this.state.posts].reverse().map(obj => (
                <div style={{ marginTop: "1rem" }}>
                  <div>{obj.profileName}</div>
                  <div>{obj.dateTime}</div>
                  <div> {obj.message}</div>
                </div>
              ))}
          </div>
        </div>
      </>
    );
  }
}

const container = () => css`
  .list {
    margin-top: 1rem;

    .message {
      padding-bottom: 1rem;
    }
  }
`;

export default MessageBoard;
