import React, { Component } from "react";
import TextInput from "./ui/TextInput";
import FormButton from "./ui/FormButton";
import FileInput from "./ui/FileInput";

//css
import { css } from "emotion";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      boy: {}
    };
  }

  componentDidMount() {
    this.getBoy();
  }

  async getBoy() {
    const urlParams = window.location.href.split("profile/:");
    const id = urlParams[1];
    const apiUrl = `http://localhost:5000/api/boys/${id}`;

    await fetch(apiUrl)
      .then(response => {
        return response.json();
      })
      .then(data => {
        const boy = data;
        this.setState({ boy: boy });
      });
  }

  render() {
    const { boy } = this.state;

    return (
      <div className={container()}>
        <h2>{boy.name}</h2>
        <h2>{boy.age}</h2>
        <div>
          <img
            className="image"
            src={process.env.PUBLIC_URL + `/profileImages/${boy.image}.jpg`}
          />
        </div>
        <div>{boy.description}</div>
      </div>
    );
  }
}

const container = () => css``;

export default Profile;
